/* If you use a database other than Mid Sweden University's PostgreSQL server, 
   you can use this SQL script to create the necessary tables and enter data.
 */
CREATE TABLE institution (
    description TEXT,
    institution TEXT NOT NULL,
    institution_code CHAR(3),
	CONSTRAINT institution_pk PRIMARY KEY (institution_code)
);

CREATE TABLE subject (
    body_text TEXT,
    preamble TEXT,
    subject TEXT NOT NULL,
    subject_code CHAR(3),
    language VARCHAR(3),
	CONSTRAINT subject_pk PRIMARY KEY (subject_code)
);

CREATE TABLE course (
    course_code CHAR(6),
    institution_code CHAR(3) NOT NULL,
    level CHAR(2) NOT NULL CHECK(level IN ('GR', 'AV', 'BE')),
    name TEXT,
    points NUMERIC(4, 1) NOT NULL,
    progression CHAR(1) CHECK(progression IN ('A', 'B', 'C')),
    subject_code CHAR(3) NOT NULL,
	CONSTRAINT course_pk PRIMARY KEY (course_code),
	CONSTRAINT course_fk1 FOREIGN KEY (institution_code) REFERENCES institution(institution_code),
	CONSTRAINT course_fk2 FOREIGN KEY (subject_code) REFERENCES subject(subject_code)
);



